import { createContext, useEffect, useState, useContext } from "react";
import { supabase } from "../"; // ⚠️ removed extra dot in your import

// 1. Create the context
const AuthContext = createContext();

// 2. Create the provider component
export const AuthProvider = ({ children }) => {
  const [session, setSession] = useState(null);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // ✅ Sign up a new user with profile
  const signUpNewUser = async (formData) => {
    const { email, password, fullName, phoneNumber, dob, gender, address } = formData;

    // 1. Create auth account
    const { data, error } = await supabase.auth.signUp({
      email,
      password,
    });

    if (error) {
      console.error("Error signing up:", error.message);
      return { success: false, error };
    }

    // 2. Insert into profiles if auth signup worked
    if (data.user) {
      const { error: profileError } = await supabase.from("profiles").insert({
        id: data.user.id, // link to auth.users
        full_name: fullName,
        phone_number: phoneNumber,
        dob,
        gender,
        address,
      });

      if (profileError) {
        console.error("Error creating profile:", profileError.message);
        return { success: false, error: profileError };
      }
    }

    return { success: true, data };
  };

  // ✅ Sign in an existing user
  const signInUser = async (email, password) => {
    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      console.error("Error signing in:", error.message);
      return { success: false, error };
    }
    return { success: true, data };
  };

  // ✅ Sign out
  const signOutUser = async () => {
    const { error } = await supabase.auth.signOut();
    if (error) {
      console.error("Error signing out:", error.message);
    }
  };

  // ✅ Get user profile (from profiles table)
  const getUserProfile = async (userId) => {
    const { data, error } = await supabase
      .from("profiles")
      .select("*")
      .eq("id", userId)
      .single();

    if (error) {
      console.error("Error fetching profile:", error.message);
      return null;
    }
    return data;
  };

  // ✅ Handle session management
  useEffect(() => {
    setLoading(true);

    const getSession = async () => {
      const {
        data: { session },
      } = await supabase.auth.getSession();

      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    };

    getSession();

    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
      setUser(session?.user ?? null);
    });

    return () => {
      subscription?.unsubscribe();
    };
  }, []);

  // ✅ Context value
  const value = {
    session,
    user,
    loading,
    signUpNewUser,
    signInUser,
    signOutUser,
    getUserProfile, // new helper
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

// 3. Custom hook
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};
