import React, { useState } from 'react';
// This path assumes 'supabaseClient.js' is in the 'src' folder.
// The path goes up two levels from the current directory ('authpage' -> 'components' -> 'src').
import { supabase } from '../../supabaseClient'; 

/**
 * The Register component provides a form for new users to sign up.
 * It captures user details and uses Supabase auth for registration.
 */
export default function Register({ onSwitchToLogin }) {
    // State variables to manage form inputs and component status
    const [loading, setLoading] = useState(false);
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [dob, setDob] = useState('');
    const [gender, setGender] = useState('');
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    // Function to handle the form submission
    const handleRegister = async (e) => {
        e.preventDefault();
        setError('');
        setMessage('');

        // Check if Supabase client is available before proceeding
        if (!supabase) {
            setError("Authentication service is not available. Please check the configuration.");
            return;
        }

        setLoading(true);
        // Call Supabase's signUp method
        const { data, error } = await supabase.auth.signUp({
            email: email,
            password: password,
            options: {
                // Additional user data is stored in the 'user_metadata' field in Supabase
                data: {
                    full_name: fullName,
                    phone: phoneNumber,
                    dob: dob,
                    gender: gender,
                }
            }
        });

        if (error) {
            setError(error.message);
        } else if (data.user) {
            // Display a success message prompting the user to check their email
            setMessage('Registration successful! Please check your email for the confirmation link to activate your account.');
        }
        setLoading(false);
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-50 py-12">
            <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-lg shadow-lg">
                <div className="text-center">
                    
                    <h1 className="text-3xl font-bold text-gray-900">Create an Account</h1>
                    <p className="mt-2 text-gray-600">Join the KMRL Knowledge Hub</p>
                </div>

                {/* Display error or success messages */}
                {error && <div className="p-3 text-sm text-red-700 bg-red-100 rounded-lg" role="alert">{error}</div>}
                {message && <div className="p-3 text-sm text-green-700 bg-green-100 rounded-lg" role="alert">{message}</div>}

                <form className="space-y-4" onSubmit={handleRegister}>
                    <InputField label="Full Name" type="text" value={fullName} onChange={(e) => setFullName(e.target.value)} required />
                    <InputField label="Email Address" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <InputField label="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    <InputField label="Phone Number" type="tel" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />
                    <InputField 
                        label="Date of Birth" 
                        type="text" 
                        placeholder="DD-MM-YYYY" 
                        value={dob} 
                        onChange={(e) => setDob(e.target.value)} 
                    />

                    <div>
                        <label className="block text-sm font-medium text-gray-700">Gender</label>
                        <select
                            value={gender}
                            onChange={(e) => setGender(e.target.value)}
                            className="w-full px-3 py-2 mt-1 placeholder-gray-400 border border-gray-300 rounded-md shadow-sm appearance-none focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                        >
                            <option value="">Select Gender</option>
                            <option value="male">Male</option>
                            <option value="female">Female</option>
                            <option value="other">Other</option>
                        </select>
                    </div>

                    <div>
                        <button
                            type="submit"
                            disabled={loading || !supabase}
                            className="relative flex justify-center w-full px-4 py-2 mt-4 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md group hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:bg-gray-400"
                        >
                            {loading ? 'Creating Account...' : 'Create Account'}
                        </button>
                    </div>
                </form>

                <p className="text-sm text-center text-gray-600">
                    Already have an account?{' '}
                    <button onClick={onSwitchToLogin} className="font-medium text-blue-600 hover:text-blue-500">
                        Sign in here
                    </button>
                </p>
            </div>
        </div>
    );
}

// Reusable component for form input fields to keep the main component cleaner
const InputField = ({ label, type, value, onChange, placeholder = '', required = false }) => (
    <div>
        <label className="block text-sm font-medium text-gray-700">{label}</label>
        <input
            type={type}
            value={value}
            onChange={onChange}
            placeholder={placeholder}
            required={required}
            className="w-full px-3 py-2 mt-1 placeholder-gray-400 border border-gray-300 rounded-md shadow-sm appearance-none focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        />
    </div>
);
